import React, { useState } from 'react'

export const ManagerLogin = () => {

    const dummyData = {
        "username": "Mngr01",
        "password": "Manager"
    }

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const mngrLogin = (e) => {
        e.preventDefault();

        if(username===dummyData.username && password===dummyData.password){
            alert(`${username} Logged successfully..`);
        } else {
            setError('Invalid Username or Password');
        }
    };

  return (
    <div>
        <h2>Login as Manager</h2>
        <form onSubmit={mngrLogin}>
            <div>
                <label>Username: </label>
                <input type='text' name='username' value={username} placeholder='Enter username...' onChange={(e) => setUsername(e.target.value)} />
            </div>
            <div>
                <label>Password: </label>
                <input type='password' name='password' value={password} placeholder='Enter password...' onChange={(e) => setPassword(e.target.value)} />
            </div>
            {error && <p style={{color: 'red'}}>{error}</p>}
            <button type='submit'>Login</button>
        </form>
    </div>
  )
}
