import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';

export const MngrHomePg = () => {
    const [recipeData, setReciepeData] = useState([]);
    const [itemData, setItemData] = useState([]);
    const [matchedItems, setMatchedItems] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
      getReciepe().then((res) => {
        setReciepeData(res.data);
      });

      getCrtRcp().then((res) => {
        setItemData(res.data);
      });
    }, []);

    useEffect(() => {
      // Reset matched items
      setMatchedItems([]);

      // Iterate over recipeData to find matches in itemData
      recipeData.forEach((recipe) => {
        const matchingItems = itemData.filter(
          (item) => item.reciepeNm === recipe.reciepeNm
        );
        setMatchedItems((prevItems) => [
          ...prevItems,
          { recipe: recipe, items: matchingItems },
        ]);
      });
    }, [recipeData, itemData]);

  return (
    <div className='flex overflow-y-hidden'>
        <div className='flex-[72%] h-[92vh] px-7 py-3'>
            <h1 className='text-[36px] font-bold '>Recipes:</h1>
            <div className='bg-orange-100 relative w-full rounded-lg h-[92%] p-3'>
                <div className='bg-orange-200 rounded-xl mb-3 p-3 flex items-center'>
                    <h2 className='text-[25px] font-semibold text-center flex-[20%] border-x border-gray-400'>Recipe Name</h2>
                    <div className='flex-[45%] text-[25px] font-semibold text-center border-r border-gray-400'>Ingridients</div>
                    <div className='flex-[15%] text-[25px] font-semibold text-center border-r border-gray-400'>Price</div>
                    <div className='flex-[20%] text-[25px] font-semibold text-center border-r border-gray-400'>Order</div>
                </div>
                <div className='h-[2px] w-full bg-orange-500'></div>
            </div>
        </div>

        <div className='flex-[28%] h-[89.5vh] px-7'>
            <h1 className='text-[36px] font-bold pt-3'>Orders:</h1>
            <div className='bg-orange-100 relative w-full rounded-lg h-[92%] p-3'>
                <div className='bg-orange-200 rounded-xl mb-3 p-3 flex items-center'>
                    <div className='text-[25px] font-semibold text-center flex-[60%] border-x border-gray-400'>Recipe Name</div>
                    <div className='text-[25px] font-semibold text-center flex-[40%] border-r border-gray-400'>Price</div>
                </div>
                <div className='h-[2px] w-full bg-orange-500'></div>
            </div>
        </div>
    </div>
  )
}
