import React, { useEffect, useState } from "react";
import { getReciepe } from "../../SERVICES/reciepeApi";
import { getCrtRcp } from "../../SERVICES/createRecApi";

export const ViewRecipe = () => {
    const [recipeData, setReciepeData] = useState([]);
    const [itemData, setItemData] = useState([]);
    const [recipeNm, setReciepeNm] = useState([]);

    useEffect( () => {
        getReciepe()
        .then( (res) => {
            setReciepeData(res.data);
        });

        getCrtRcp()
        .then( (res) => {
            setItemData(res.data);
        });
    }, []);

    useEffect( () => {
        recipeData?.map( (data) => {
            setReciepeNm(data.reciepeNm);
        })
    });
    console.log(recipeNm)

  return (
    <div>
      {/* STOCK DATA */}
      <div className="my-3 w-[800px] m-auto">
        <h1 className="text-center text-[36px] font-bold">RECIPES: </h1>

        {recipeData?.map( (data, i) => (
        <div key={i} className="bg-white my-2 flex items-center rounded-xl border border-orange-500">
                <h1 className="text-center py-2 px-4 flex-[25%] text-[36px] font-bold">{data.reciepeNm}</h1>

            <div className="flex-[75%] py-2 px-4 border-l border-orange-500">
                <div className="text-xl font-semibold">Ingridents Used:</div>
                <ul className="text-lg px-2">
                    <li>Item1</li>
                    <li>Item1</li>
                </ul>
            </div>
        </div>
        ))}

      </div>
    </div>
  );
}
