import React, { useState, useEffect } from "react";
// import "../../App.css";
import { useNavigate } from "react-router-dom";
// import { addReciepe, getReciepe } from "../../../SERVICES/reciepeApi";
import foodRecipe from "../../../IMAGES/recipe.png";
import { getCrtRcp, getOldReciepeById } from "../../../SERVICES/createRecipeAPI";

export const UpdtRecipe = () => {
  const [reciepeNm, setReciepeNm] = useState("");
  const [reciepeId, setReciepeId] = useState(null);
  const [err, setErr] = useState("");

  const [oldData, setOldData] = useState([]);
  const navigate = useNavigate();

  useEffect( () => {
    getCrtRcp().then( (resp) => {
      setOldData(resp.data);
    });
  }, []);

  console.log(oldData)

  // useEffect( () => {

  // }, [])
  // console.log(reciepeId);



  const handleSubmit = async (e) => {
    e.preventDefault();
    setErr("");
    setReciepeNm("");
    oldData?.map( (dt) => {
      console.log(dt.reciepeNm);
      if( reciepeNm === dt.reciepeNm){
        console.log(dt.reciepeId);
        setReciepeId(dt.reciepeId);
        navigate(`/oldreciepe/${dt.reciepeId}`);
      }
    })

    // try {
    //   const newReciepe = { reciepeNm };
    //   const response = await getCrtRcp({
    //     where: {
    //       reciepeNm: newReciepe
    //     }
    //   }).then( (resp) => {
    //     const reciepeId = resp.data[0].reciepeId;
    //     console.log(resp.data[0].reciepeId);
    //   });
    // } catch (error) {
    //   setErr(error.response?.data?.message || "Failed to add vendor");
    // }
  };

  return (
    <div className="flex lg:flex-row flex-col h-[88vh] items-center justify-center">
      <div className="mt-6">
        <img className="w-[500px] h-[460px]" src={foodRecipe} alt="Admin" />
      </div>

      <div className="">
        <div className="bg-orange-300 rounded-xl mt-4 py-4 px-5">
          <h2 className="text-[30px] text-center font-bold">Update Recipe</h2>
          <form onSubmit={handleSubmit} className="text-xl ">
            <div className="my-2">
              <label>Reciepe Name:</label>
              <input
                className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
                type="text"
                name="reciepeNm"
                value={reciepeNm}
                placeholder="Enter name to your new recipe :)"
                onChange={(e) => setReciepeNm(e.target.value)}
              />
            </div>
            {err && <p style={{ color: "red" }}>Recipe name is mandatory</p>}
            <div className="max-w-[180px] w-auto m-auto">
              <button
                className="bg-orange-200 px-5 font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2"
                type="submit"
              >
                Update Recipe
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};
