import React, { useEffect, useState } from "react";
import { getVendors } from "../../SERVICES/api";
import "../../App.css";
import { additems } from "../../SERVICES/itemsAPI";
import { useNavigate } from "react-router-dom";

export const AddItems = () => {
  const [items, setItems] = useState([]);
  const [vendorNm, setVendorNm] = useState("");
  const [itemNm, setItemnm] = useState("");
  const [quantity, setQuantity] = useState("");
  const navigate = useNavigate();

  const [array, setArray] = useState([]);
  localStorage.setItem("items", JSON.stringify(array));

  useEffect(() => {
    getVendors().then((resp) => {
      setItems(resp.data);
      // console.log(resp.data)
    });
  }, []);

  const AddToDB = async (e) => {
    e.preventDefault();
    if (!vendorNm || !itemNm || !quantity) {
      alert("Please fill all fields.");
      return;
    }
    try {
      const newItem = { vendorNm, itemNm, quantity };
      setArray([...array, newItem]);
      await additems(newItem);

      setItemnm("");
      setQuantity("");
    } catch (error) {
      console.error("Error adding item:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!vendorNm || !itemNm || !quantity) {
      alert("Please fill all fields.");
      return;
    }
    try {
      const newItem = { vendorNm, itemNm, quantity };
      await additems(newItem);

      setVendorNm("");
      setItemnm("");
      setQuantity("");

      localStorage.removeItem("items");
      navigate("/adminHome");
    } catch (error) {
      console.error("Error adding item:", error);
    }
  };

  return (
    <div className="border border-black">
      <form
        onSubmit={handleSubmit}
        className="text-xl border border-black w-[500px] m-auto my-5 bg-orange-300 rounded-xl py-4 px-5"
      >
        <h2 className="text-[36px] font-bold">Add Items</h2>
        <div className="my-2">
          <label>Vendor's Name: </label>
          <input
            className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
            type="text"
            name="vendorNm"
            value={vendorNm}
            placeholder="Enter vendor's name here..."
            onChange={(e) => setVendorNm(e.target.value)}
          />
        </div>
        <div>
          <div className="my-2">
            <label>Add Items: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="text"
              name="itemNm"
              value={itemNm}
              placeholder="Enter items name..."
              onChange={(e) => setItemnm(e.target.value)}
            />
          </div>
          <div className="my-2">
            <label>Quantity: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="number"
              name="quantity"
              value={quantity}
              placeholder="Enter items quantity..."
              onChange={(e) => setQuantity(e.target.value)}
            />
          </div>

          <div className="max-w-[180px] w-auto my-3 m-auto">
            <button className="bg-orange-200 px-5 text-lg font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2" onClick={AddToDB}>Add More Items</button>
          </div>
        </div>

        <div className="max-w-[150px] w-auto my-3 m-auto text-lg">
          <button className="bg-orange-200 px-5 font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2" type="submit">Add Items</button>
        </div>
      </form>

      {array.length > 0 && (
        <>
          <h2>Items added:</h2>
          {array.map((a, i) => (
            <span className="subSpan" key={i}>
              ({a.quantity} kg) {a.itemNm}
            </span>
          ))}
        </>
      )}

      <h1>Vendor's List:</h1>
      <table>
        <tr>
          <th>Sr. No.</th>
          <th>Vendor's Name</th>
          <th>Phone No.</th>
        </tr>
        {items.map((item, index) => (
          <tr key={item._id}>
            <td>{index + 1}</td>
            <td>{item.vendors}</td>
            <td>{item.phone}</td>
          </tr>
        ))}
      </table>
    </div>
  );
};
