import React, { useEffect, useState } from "react";
import { getReciepeById } from "../../SERVICES/reciepeApi";
import { useNavigate, useParams } from "react-router-dom";
import { addCreateReciepe } from "../../SERVICES/createRecipeAPI";

export const CreateReciepe = () => {
  const { reciepeId } = useParams();
  const [reciepeNm, setReciepe] = useState("");
  const [itemNm, setItemNm] = useState("");
  const [quantity, setQuantity] = useState("");
  const [err, setErr] = useState("");
  const navigate = useNavigate();

  const [array, setArray] = useState([]);
  localStorage.setItem("items", JSON.stringify(array));

  useEffect(() => {
    if (reciepeId) {
      getReciepeById(reciepeId)
        .then((resp) => {
          setReciepe(resp.data.reciepeNm);
        })
        .catch(() => {
          setErr("Error fetching the recipe");
        });
    }
  }, [reciepeId]);

  const handleSUbmit = async (e) => {
    e.preventDefault();
    if (!itemNm || !quantity) {
      setErr("Please fill all fields.");
      return;
    }
    try {
      const newItem = { reciepeNm, itemNm, quantity };
      await addCreateReciepe(newItem);
      navigate("/adminHome");
    } catch (e) {
      setErr("Error adding item.");
    }
  };

  const AddToDB = async (e) => {
    e.preventDefault();
    if (!itemNm || !quantity) {
      setErr("Please fill all fields.");
      return;
    }
    try {
      const newItem = { reciepeNm, itemNm, quantity };
      setArray([...array, newItem]);
      await addCreateReciepe(newItem);

      setItemNm("");
      setQuantity("");
    } catch (e) {
      setErr("Error adding item.");
    }
  };

  return (
    <div className="flex lg:flex-row flex-col h-[88vh] items-center justify-center">
      {/* <div className="mt-6">
        <img className="w-[500px] h-[460px]" src={foodRecipe} alt="Admin" />
      </div> */}

      <div className="bg-orange-300 rounded-xl mt-4 py-4 px-5">
        <h2 className="text-[30px] text-center font-bold">
          Create Recipe: {reciepeNm}
        </h2>
        <form onSubmit={handleSUbmit}>
          <div className="my-2">
            <label>Required Items: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="text"
              name="itemNm"
              value={itemNm}
              placeholder="Enter items name here..."
              onChange={(e) => setItemNm(e.target.value)}
            />
          </div>
          <div className="my-2">
            <label>Required Quantity: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="number"
              name="quantity"
              value={quantity}
              placeholder="Enter quantity of required item..."
              onChange={(e) => setQuantity(e.target.value)}
            />
          </div>
          <div className="max-w-[180px] w-auto my-3 m-auto">
            <button
              className="bg-orange-200 px-5 text-lg font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2"
              onClick={AddToDB}
            >
              Add More Items
            </button>
          </div>
          {err && <p style={{ color: "red" }}>{err}</p>}
          <div className="max-w-[125px] w-auto m-auto text-lg">
            <button
              className="bg-orange-200 px-5 font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2"
              type="submit"
            >
              Purchase
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};
