import React, { useState } from 'react';
import '../../App.css';
import { useNavigate } from 'react-router-dom';
import { addReciepe } from '../../SERVICES/reciepeApi';

export const AddReciepe = () => {

    const [reciepeNm, setReciepeNm] = useState('');
    const [err, setErr] = useState('')
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErr('');

        try{
            const newReciepe = {reciepeNm};
            const response = await addReciepe(newReciepe)
            const reciepeId = response.data._id;
            setReciepeNm('');
            navigate(`/createreciepe/${reciepeId}`)
        }catch(error){
            setErr(error.response?.data?.message || 'Failed to add vendor');
        }
    }

    return (
        // <div>
        //     <form className='reciepeForm' onSubmit={handleSubmit}>
        //         <h1>Add Reciepe</h1>
        //         <button type='submit'>Create Reciepe</button>
        //     </form>
        // </div>

        <div className="flex flex-col h-[90vh] items-center"> 
      {/* <div className="mt-6">
        <img src={aDmIn} alt="Admin" />
      </div> */}
      <div className="bg-orange-300 rounded-xl w-[500px] mt-4 m-auto py-4 px-5">
        <h2 className="text-[30px] text-center font-bold">Add Recipe</h2>
        <form onSubmit={handleSubmit} className="text-xl ">

        <div className="my-2">
            <label>Reciepe Name:</label>
            <input className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1" type='text' name='reciepeNm' value={reciepeNm} placeholder='Add title to your reciepe...' onChange={(e) => setReciepeNm(e.target.value)} />
        </div>
        {err && <p style={{color: 'red'}}>Recipe name is mandatory</p>}
          <div className="max-w-[100px] w-auto m-auto">
            <button
              className="bg-orange-200 px-5 font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2"
              type="submit"
            >
              Create Recipe
            </button>
          </div>
        </form>
      </div>
    </div>
    )
}
