import React, { useEffect, useState } from "react";
import { getReciepe } from "../../SERVICES/reciepeApi";
import { getCrtRcp } from "../../SERVICES/createRecApi";

export const ViewRecipe = () => {
    const [recipeData, setReciepeData] = useState([]);
    const [itemData, setItemData] = useState([]);
    const [matchedItems, setMatchedItems] = useState([]);

    useEffect( () => {
        getReciepe()
        .then( (res) => {
            setReciepeData(res.data);
        });

        getCrtRcp()
        .then( (res) => {
            setItemData(res.data);
        });
    }, []);

    useEffect(() => {
        // Reset matched items
        setMatchedItems([]);

        // Iterate over recipeData to find matches in itemData
        recipeData.forEach((recipe) => {
          const matchingItems = itemData.filter((item) => item.reciepeNm === recipe.reciepeNm);
          setMatchedItems((prevItems) => [...prevItems, { recipe: recipe, items: matchingItems }]);
        });
      }, [recipeData, itemData]);

  return (
    <div>
      {/* STOCK DATA */}
      <div className="my-3 w-[800px] m-auto">
        <h1 className="text-center text-[36px] font-bold">RECIPES: </h1>

        {matchedItems.map((match, index) => (
          <div key={index} className="bg-white my-2 flex items-center rounded-xl border border-orange-500">
            <h1 className="text-center py-2 px-4 flex-[25%] text-[36px] font-bold">{match.recipe.reciepeNm}</h1>

            <div className="flex-[75%] py-2 px-4 border-l border-orange-500">
              <div className="text-xl font-semibold">Ingredients Used:</div>
              <ul className="text-lg px-2">
                {match.items.map((item, idx) => (
                  <li key={idx}>{item.itemNm}</li>
                ))}
              </ul>
            </div>
          </div>
        ))}

      </div>
    </div>
  );
}
