import React, { useEffect, useState } from "react";
import { getVendors } from "../../SERVICES/api";
import "../../App.css";
import { additems } from "../../SERVICES/itemsAPI";
import { useNavigate } from "react-router-dom";import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { getItems } from "../../SERVICES/itemsAPI";
import '../../App.css';

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: 'rgb(255, 131, 86)',
    color: 'white',
    textAlign: 'center',
    fontSize: 18,
    fontWeight: 600,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 16,
    textAlign: 'center',
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: 'rgb(255, 224, 199)',
  },
}));

export const AddItems = () => {
  const [items, setItems] = useState([]);
  const [vendorNm, setVendorNm] = useState("");
  const [itemNm, setItemnm] = useState("");
  const [quantity, setQuantity] = useState("");
  const navigate = useNavigate();

  const [array, setArray] = useState([]);
  localStorage.setItem("items", JSON.stringify(array));

  useEffect(() => {
    getVendors().then((resp) => {
      setItems(resp.data);
    });
  }, []);

  const AddToDB = async (e) => {
    e.preventDefault();
    if (!vendorNm || !itemNm || !quantity) {
      alert("Please fill all fields.");
      return;
    }
    try {
      const newItem = { vendorNm, itemNm, quantity };
      setArray([...array, newItem]);
      await additems(newItem);

      setItemnm("");
      setQuantity("");
    } catch (error) {
      console.error("Error adding item:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!vendorNm || !itemNm || !quantity) {
      alert("Please fill all fields.");
      return;
    }
    try {
      const newItem = { vendorNm, itemNm, quantity };
      await additems(newItem);

      setVendorNm("");
      setItemnm("");
      setQuantity("");

      localStorage.removeItem("items");
      navigate("/adminHome");
    } catch (error) {
      console.error("Error adding item:", error);
    }
  };

  return (
    <div className="flex">
      <form
        onSubmit={handleSubmit}
        className="flex[50%] text-xl w-[500px] m-auto my-5 bg-orange-300 rounded-xl py-4 px-5"
      >
        <h2 className="text-[36px] text-center my-2 font-bold">Add Items</h2>
        <div className="my-2">
          <label>Vendor's Name: </label>
          <input
            className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
            type="text"
            name="vendorNm"
            value={vendorNm}
            placeholder="Enter vendor's name here..."
            onChange={(e) => setVendorNm(e.target.value)}
          />
        </div>
        <div>
          <div className="my-2">
            <label>Add Items: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="text"
              name="itemNm"
              value={itemNm}
              placeholder="Enter items name..."
              onChange={(e) => setItemnm(e.target.value)}
            />
          </div>
          <div className="my-2">
            <label>Quantity: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="number"
              name="quantity"
              value={quantity}
              placeholder="Enter items quantity..."
              onChange={(e) => setQuantity(e.target.value)}
            />
          </div>

          <div className="max-w-[180px] w-auto my-3 m-auto">
            <button className="bg-orange-200 px-5 text-lg font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2" onClick={AddToDB}>Add More Items</button>
          </div>
        </div>

        <div className="max-w-[125px] w-auto m-auto text-lg">
          <button className="bg-orange-200 px-5 font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2" type="submit">Purchase</button>
        </div>
      </form>

      {array.length > 0 && (
        <>
          <h2>Items added:</h2>
          {array.map((a, i) => (
            <span className="subSpan" key={i}>
              ({a.quantity} kg) {a.itemNm}
            </span>
          ))}
        </>
      )}

      {/* <div className="w-full mt-7 h-[2px] bg-orange-600"></div> */}

      <div className="my-3 w-[800px] m-auto">
        <h1 className="text-center text-[36px] font-bold">Vendor's List: </h1>
        <TableContainer className="" component={Paper}>
          <Table sx={{ minWidth: 700 }} aria-label="customized table">
            <TableHead>
              <TableRow className="tr">
                <StyledTableCell>Ingridient</StyledTableCell>
                <StyledTableCell>Quantity Left</StyledTableCell>
                <StyledTableCell>Vendor's Name</StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {items?.map((row, index) => (
                <StyledTableRow key={row._id}>
                  <StyledTableCell component="th" scope="row">
                    {index + 1}
                  </StyledTableCell>
                  <StyledTableCell component="th" scope="row">
                    {row.vendors}
                  </StyledTableCell>
                  <StyledTableCell align="right">
                    {row.phone}
                  </StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    </div>
  );
};
