import React, { useEffect, useState } from 'react'
import { getReciepeById } from '../../SERVICES/reciepeApi';
import { useNavigate, useParams } from 'react-router-dom';
import { addCreateReciepe } from '../../SERVICES/createRecipeAPI';

export const CreateReciepe = () => {

  const { reciepeId } = useParams();
  const [reciepeNm, setReciepe] = useState('');
  const [itemNm, setItemNm] = useState('');
  const [quantity, setQuantity] = useState('');
  const [err, setErr] = useState('');
  const navigate = useNavigate();

  const [array, setArray] = useState([]);
  localStorage.setItem('items', JSON.stringify(array));

  useEffect(() => {
    if (reciepeId) {
      getReciepeById(reciepeId).then((resp) => {
        setReciepe(resp.data.reciepeNm);
      }).catch(() => {
        setErr("Error fetching the recipe");
      });
    }
  }, [reciepeId]);

  const handleSUbmit = async (e) => {
    e.preventDefault();
    if (!itemNm || !quantity) {
      setErr('Please fill all fields.');
      return;
    }
    try {
      const newItem = { reciepeNm, itemNm, quantity };
      await addCreateReciepe(newItem);
      navigate('/adminHome')
    } catch (e) {
      setErr('Error adding item.');
    }
  }

  const AddToDB = async (e) => {
    e.preventDefault();
    if (!itemNm || !quantity) {
      setErr('Please fill all fields.');
      return;
    }
    try {
      const newItem = { reciepeNm, itemNm, quantity };
      setArray([...array, newItem])
      await addCreateReciepe(newItem);

      setItemNm('');
      setQuantity('');
    } catch (e) {
      setErr('Error adding item.');
    }
  };

  return (
    <div>
      <h1>Reciepe: {reciepeNm}</h1>
      <form onSubmit={handleSUbmit}>
        <div>
          <label>Required Items: </label>
          <input type='text' name='itemNm' value={itemNm} placeholder='Enter items name here...' onChange={(e) => setItemNm(e.target.value)} />
        </div>
        <div>
          <label>Required Quantity: </label>
          <input type='number' name='quantity' value={quantity} placeholder='Enter quantity of required item...' onChange={(e) => setQuantity(e.target.value)} />
        </div>
        <div>
          <button onClick={AddToDB}>+</button>
        </div>
        {err && <p style={{color: 'red'}}>{err}</p>}
        <button>Create </button>
      </form>
    </div>
  )
}
