import React, { useState } from 'react'
import { addVendor } from '../../SERVICES/api';
import { useNavigate } from 'react-router-dom';

export const AddVendors = () => {

    const [formData, setFormData] = useState({
        vendors: '', phone:''
    });
    const [err, setErr] = useState('');
    const navigate = useNavigate();

    const handleChange = (e) => {
        const {name, value} = e.target;
        setFormData({...formData, [name]: value});
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setErr('');

        try {
            const response = await addVendor({
                vendors: formData.vendors,
                phone: formData.phone
            });
            console.log(response)
            setFormData({ vendors:'', phone:'' });
            navigate('/adminHome')
        } catch (error) {
            setErr(error.response?.data?.message || 'Failed to add vendor');
        }
    }

    return (
        // <div>
        //     <h1>AddVendors</h1>
        //     <form onSubmit={handleSubmit}>
        //         <div>
        //             <label>Vendor's Name: </label>
        //             <input type='text' name='vendors' value={formData.vendors} placeholder="Enter vendor's name..." onChange={handleChange} />
        //         </div>
        //         <div>
        //             <label>Phone No.: </label>
        //             <input type='number' name='phone' value={formData.phone} placeholder="Enter phone number..." onChange={handleChange} />
        //         </div>
        //         {err && <p style={{color: 'red'}}>{err}</p>}
        //         <button type='submit'>Add Vendor</button>
        //     </form>
        // </div>

        <div className="flex flex-col h-[90vh] items-center"> 
      {/* <div className="mt-6">
        <img src={aDmIn} alt="Admin" />
      </div> */}
      <div className="bg-orange-300 rounded-xl w-[500px] mt-4 m-auto py-4 px-5">
        <h2 className="text-[30px] text-center font-bold">Add Vendors</h2>
        <form onSubmit={handleSubmit} className="text-xl ">
          <div className="my-2">
            <label>Username: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="text"
              name="username"
              value={username}
              placeholder="Enter Username..."
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="my-3">
            <label>Password: </label>
            <input
              className="w-full rounded-lg text-lg focus:border-b-2 border-orange-600 outline-none p-2 bg-orange-200 my-1"
              type="password"
              name="password"
              value={password}
              placeholder="Enter password..."
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          {error && <p style={{ color: "red" }}>{error}</p>}
          <div className="max-w-[100px] w-auto m-auto">
            <button
              className="bg-orange-200 px-5 font-semibold rounded-lg hover:bg-orange-500 hover:text-white py-2"
              type="submit"
            >
              Login
            </button>
          </div>
        </form>
      </div>
    </div>
    )
}
